import React, { Component } from 'react'

class EventBind extends Component {
    constructor() {
        super()

        this.state = {
             message:'Hello'
        }
    }
    clickHandler(){
        this.setState({
            message:"Good Bye"
        })
        console.log(this)
    }

    render() {
        return (
            <div>
                <div>{this.state.message}</div>
                <button onClick={this.clickHandler.bind(this)}>Click</button>
            </div>
        )
    }
}

export default EventBind
